DROP TABLE IF EXISTS `ProductCopy`;
CREATE TABLE `ProductCopy` (
  `productID` int(11) NOT NULL AUTO_INCREMENT COMMENT '产品ID',
  `storeID` int(11) DEFAULT NULL COMMENT '店铺ID',
  `productName` varchar(255) COLLATE utf8_general_mysql500_ci DEFAULT NULL COMMENT '拍品名称',
  `listImg` varchar(255) COLLATE utf8_general_mysql500_ci DEFAULT NULL,
  `detailImgs` text COLLATE utf8_general_mysql500_ci COMMENT '详情图片',
  `productDetail` text COLLATE utf8_general_mysql500_ci COMMENT '拍品介绍',
  `productCategoryID` int(11) DEFAULT NULL COMMENT '拍品分类ID',
  `startPrice` decimal(9,2) DEFAULT NULL COMMENT '起拍价',
  `auctionPerPrice` decimal(9,2) DEFAULT NULL COMMENT '单次出价',
  `currPrice` decimal(9,2) DEFAULT NULL COMMENT '当前价格',
  `deliveryPrice` decimal(9,2) DEFAULT NULL COMMENT '运费',
  `commision` decimal(9,2) DEFAULT NULL COMMENT '佣金',
  `existKeepPrice` tinyint(4) DEFAULT '0' COMMENT '是否存在保留价',
  `keepPrice` decimal(9,2) DEFAULT NULL COMMENT '保留价',
  `delayType` tinyint(1) DEFAULT '2' COMMENT '延时周期【1：5分钟/次, 2：不延时】',
  `auctionCount` int(11) DEFAULT '0' COMMENT '出价次数',
  `startTime` int(11) DEFAULT NULL COMMENT '起拍时间',
  `endTime` int(11) DEFAULT NULL COMMENT '结束时间',
  `auctionStatus` tinyint(1) DEFAULT '0' COMMENT '状态【0：在库, 等待上架, 1：即将开始, 2：正在进行中, 3：已结束】',
  `verifyStatus` tinyint(1) DEFAULT '1' COMMENT '审核状态【1：未审核，2：通过，3：拒绝】',
  `auctionAuth` tinyint(1) DEFAULT NULL COMMENT '是否有文物拍卖资质',
  `fidelity` tinyint(1) DEFAULT NULL COMMENT '是否保真',
  `coverDelivery` tinyint(1) DEFAULT NULL COMMENT '是否包邮',
  `refundInDays` tinyint(1) DEFAULT NULL COMMENT '三日内可退',
  `certificateCard` tinyint(1) DEFAULT NULL COMMENT '是否有鉴定证书',
  `authorAuth` tinyint(1) DEFAULT NULL COMMENT '作者授权',
  `isRecommend` tinyint(4) DEFAULT '0' COMMENT '是否推荐',
  `isIndexRecommend` tinyint(4) DEFAULT '0' COMMENT '是否首页推荐',
  `isStoreRecommend` tinyint(4) DEFAULT '0' COMMENT '是否是店铺精选',
  `isSpecialRecommend` tinyint(4) DEFAULT '0' COMMENT '是否是专场推荐',
  `isSpecialTodayRecommend` tinyint(4) DEFAULT '0' COMMENT '是否专场拍卖推荐',
  `isProductIndexRecommend` tinyint(4) DEFAULT '0' COMMENT '是否拍品列表页推荐',
  `isMemberRecommend` tinyint(4) DEFAULT '0' COMMENT '是否个人中心推荐',
  `recommendStartTime` int(11) DEFAULT NULL COMMENT '推荐开始时间',
  `recommendEndTime` int(11) DEFAULT NULL COMMENT '推荐结束时间',
  `firstStoreCategoryID` int(11) DEFAULT NULL COMMENT '第一店铺分类ID',
  `secondStoreCategoryID` int(11) DEFAULT NULL COMMENT '第二店铺分类ID',
  `thirdStoreCategoryID` int(11) DEFAULT NULL COMMENT '第三店铺分类ID',
  `specialID` int(11) DEFAULT NULL COMMENT '专场ID',
  `storePlateForm` tinyint(1) DEFAULT NULL COMMENT '店铺平台【1：店内分类，2：专场】',
  `interestedCount` int(11) DEFAULT '0' COMMENT '关注人数',
  `deliveryCityID` int(11) DEFAULT NULL COMMENT '发货城市ID',
  `unitePayID` varchar(16) COLLATE utf8_general_mysql500_ci DEFAULT NULL COMMENT '支付号',
  `payType` tinyint(4) DEFAULT NULL COMMENT '支付类型【1：余额，2：支付宝，3：银联，4：银行汇款, 5：微信】',
  `isPaid` tinyint(4) DEFAULT '0' COMMENT '是否支付',
  `isDel` tinyint(4) DEFAULT '0' COMMENT '是否已删除',
  `viewCount` int(11) DEFAULT '0' COMMENT '阅读次数',
  `soldStatus` tinyint(4) DEFAULT '0' COMMENT '1：流拍, 2：被竞得',
  `unSoldTime` int(11) DEFAULT NULL COMMENT '流拍时间',
  `soldTime` int(11) DEFAULT NULL COMMENT '被竞得时间',
  `publishAtOnce` tinyint(4) DEFAULT '0' COMMENT '是否立即上架',
  `publishStartTime` int(11) DEFAULT NULL COMMENT '定时上架开始时间',
  `publishEndTime` int(11) DEFAULT NULL COMMENT '定时上架结束时间',
  `isIllegal` tinyint(4) DEFAULT '0' COMMENT '是否被举报【2：被举报，1：举报被处理，0：未举报】',
  `instime` timestamp NULL DEFAULT CURRENT_TIMESTAMP,
  PRIMARY KEY (`productID`)
) ENGINE=InnoDB AUTO_INCREMENT=321 DEFAULT CHARSET=utf8 COLLATE=utf8_general_mysql500_ci;

