DROP TABLE IF EXISTS `Special`;
CREATE TABLE `Special` (
  `specialID` int(11) NOT NULL AUTO_INCREMENT COMMENT '专场id',
  `specialName` varchar(50) COLLATE utf8_general_mysql500_ci DEFAULT NULL COMMENT '专场名称',
  `specialProductCategoryID` int(11) DEFAULT NULL COMMENT '专场拍品分类ID',
  `specialImg` varchar(255) COLLATE utf8_general_mysql500_ci DEFAULT NULL COMMENT '专场海报',
  `specialBanner` varchar(255) COLLATE utf8_general_mysql500_ci DEFAULT NULL COMMENT '专场首焦',
  `specialDesc` text COLLATE utf8_general_mysql500_ci COMMENT '专场介绍',
  `productCount` int(11) DEFAULT '0' COMMENT '拍品数量',
  `productCountLimit` int(11) DEFAULT NULL COMMENT '拍品数量限制',
  `viewCount` int(11) DEFAULT '0' COMMENT '围观人数',
  `interestedCount` int(11) DEFAULT '0' COMMENT '关注人数',
  `startTime` int(11) DEFAULT NULL COMMENT '开始时间',
  `endTime` int(11) DEFAULT NULL COMMENT '结束时间',
  `storeID` int(11) DEFAULT NULL COMMENT '选送商家ID',
  `auctionStatus` tinyint(1) DEFAULT '0' COMMENT '状态【1：即将开始, 2：正在进行中, 3：已结束】',
  `verifyStatus` tinyint(4) DEFAULT '0' COMMENT '审核状态【0：未提交审核, 1：待审核, 2：审核通过, 3：审核未通过】',
  `isDel` tinyint(4) DEFAULT '0' COMMENT '是否已删除',
  `unitePayID` varchar(16) COLLATE utf8_general_mysql500_ci DEFAULT NULL COMMENT '支付号',
  `payType` tinyint(4) DEFAULT NULL COMMENT '支付类型【1：余额，2：支付宝，3：银联，4：银行汇款, 5：微信】',
  `isPaid` tinyint(4) DEFAULT '0' COMMENT '是否支付',
  `denyReason` varchar(255) COLLATE utf8_general_mysql500_ci DEFAULT NULL COMMENT '不通过的原因',
  `instime` timestamp NULL DEFAULT CURRENT_TIMESTAMP,
  PRIMARY KEY (`specialID`)
) ENGINE=InnoDB AUTO_INCREMENT=66 DEFAULT CHARSET=utf8 COLLATE=utf8_general_mysql500_ci;

