INSERT INTO `Store` VALUES ('4', '10', '彩金', '专业经营西画', '', '/uploads/store-verify/577dc19adcf1f.jpeg', '/uploads/store-verify/5767751621c8b.jpeg', '/uploads/store-verify/57677516281a2.jpeg', '1', '1', '2', null, '330106197103060420', '/uploads/store-verify/576759412c59c.jpeg', '/uploads/store-verify/576759412c9ad.jpeg', '13588038833', '2848590814', '0', null, null, null, null, null, null, '2016-06-20 10:47:29'), ('5', '6', '测试', '测试', '<p>测试</p>', '/uploads/store-verify/576769d158a08.png', '/uploads/store-verify/5767e32ab5de5.png', '/uploads/store-verify/5767760d5afec.png', '0', '0', '2', null, '3624122212', '/uploads/store-verify/5767595d445db.png', '/uploads/store-verify/5767595d44784.png', '13819149852', null, '0', null, '2', null, null, null, null, '2016-06-20 10:47:57'), ('6', '11', '夜宴', '夜宴', '<p>“开物观珍品，源远而艺长”杭州开源拍卖有限公司是一家专业从事书画艺术品拍卖的机构。公司于2003年3月成立，公司秉承“源元，至诚，唯美”的经营原则，推选名家精品，公平公正的引导艺术品收藏投资，确保客户长久利益和社会价值，为客户的艺术品投资创造最高的经济价值。我们长期以来致力于艺术品流通环节，整合各方优势资源，开拓进取，为收藏家和艺术品从业机构提供一个物畅其流、物尽其用的高层次、高品质的健康、良性、高效的流通平台。为繁荣中国艺术品市场，弘扬中华民族文化，提高中国艺术品在世界上的地位和影响贡献自己的一份力量。公司严格遵守《中华人民共和国拍卖法》及相关的法律、法规。遵守“诚实守信、开拓进取、规范操作、服务社会”的经营理念，取得了良好的社会效益和经济效益。</p><p>开源拍卖立足收藏圈，经营项目涵盖书画、金石篆刻、翡翠玉雕、陶瓷、工艺美术大师作品等；随着国内艺术品市场的蓬勃发展与多元化需求，公司以持续的创新力开辟细化专场，率先推出西泠名家篆刻、名家书画、工美大师作品等专业性很强的艺术专场，为细化审美视野，引导专项收藏做出积极贡献。</p><p><br/></p>', '/uploads/store-verify/5768c630c36b2.jpeg', '/uploads/store-verify/5768c630c46cc.jpeg', '/uploads/store-verify/5768c630c5477.jpeg', '1', '1', '2', null, '33010619730504041X', '/uploads/store-verify/576759b130ca0.png', '/uploads/store-verify/576759b1318e2.png', '13858192489', '1297329443', '0', null, '2', '黄逸闻', '057185819321', '杭州市朝晖路179号', null, '2016-06-20 10:49:21'), ('7', '9', '静芙蕖斋', '专业经营西泠名家篆刻做品，并长期代理名家书画，欢迎藏家入内鉴赏。', '<p style=\"line-height: 1.5em;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&quot;开物观珍品，源远而艺长&quot;杭州开源拍卖有限公司是一家专业从事书画艺术品拍卖的机构，注册资金1000万元，拥有国家二级文物拍卖资质。公司于2003年3月成立，公司秉承&quot;源元，至诚，唯美&quot;的经营原则，推选名家精品，公平公正的引导艺术品收藏投资，确保客户长久利益和社会价值，为客户的艺术品投资创造最高的经济价值。